parameters.query:
INSERT INTO networkdeploy_topology_snapshots (task_name, topology)
VALUES (
  '{{ $json.task_name }}',
  '{{ JSON.stringify($json.topology) }}'
);

parameters.jsCode:
// ðŸ” èŽ·å–æ‰€æœ‰è¾“å…¥æ¡ç›®
const items = $input.all();

// ðŸ” ç¬¬ä¸€æ­¥ï¼šæž„å»º hostname â†’ role çš„æ˜ å°„è¡¨
const hostnameToRole = {};

for (const item of items) {
  const node = item.json?.topology?.node;
  if (node?.hostname && node?.role) {
    hostnameToRole[node.hostname] = node.role;
  }
}

// âœ… ç¬¬äºŒæ­¥ï¼šä¸ºæ¯ä¸ª link å¡«å…… targetRole
const enriched = items.map(item => {
  const topology = item.json.topology || {};
  const node = topology.node || {};
  const links = topology.links || [];

  const enrichedLinks = links.map(link => {
    const target = link.target || "unknown";
    return {
      ...link,
      targetRole: hostnameToRole[target] || "unknown"
    };
  });

  return {
    json: {
      task_name: item.json.task_name || `topology_${Date.now()}`,
      topology: {
        node,
        links: enrichedLinks
      }
    }
  };
});

// âœ… è¿”å›žè¡¥å…¨åŽçš„ç»“æžœ
return enriched;

const nodes = $json.nodes || [];
const links = $json.links || [];

// åˆ›å»º hostname â†’ node æ˜ å°„
const nodeMap = {};
nodes.forEach(n => {
  nodeMap[n.hostname] = { ...n, neighbors: [] };
});

// éåŽ† links å¡«å…… neighbors
for (const link of links) {
  const { source, target, sourcePort, targetPort, targetRole } = link;

  if (nodeMap[source]) {
    nodeMap[source].neighbors.push({
      target,
      targetRole,
      localPort: sourcePort,
      remotePort: targetPort
    });
  }

  if (nodeMap[target]) {
    nodeMap[target].neighbors.push({
      target: source,
      targetRole: nodeMap[source]?.role || "unknown",
      localPort: targetPort,
      remotePort: sourcePort
    });
  }
}

return [{
  json: {
    ...$json,
    nodes: Object.values(nodeMap)
  }
}];

const devices = ($json.body?.devices || []).filter(d => d.role !== 'OOB');

if (!Array.isArray(devices)) {
  throw new Error("è¯·æ±‚ä½“æ ¼å¼é”™è¯¯ï¼Œå¿…é¡»åŒ…å«æ•°ç»„å½¢å¼çš„ devices å­—æ®µ");
}

const ipList = devices.map(d => d.ip);

// è½¬æ¢æˆ ip â†’ { hostname, role }
const ipMap = {};
devices.forEach(d => {
  ipMap[d.ip] = { hostname: d.hostname, role: d.role };
});

return [{ json: { ipList, ipMap } }];

parameters.query:
SELECT ip_address, lldp_neighbors, hostname
FROM networkdeploy_devices
WHERE ip_address IN ({{ $json.ipList.map(ip => `'${ip}'`).join(",") }});

parameters.jsCode:
// æž„é€  node ä¿¡æ¯
const node = {
  ip: $json.ip_address || null,
  hostname: $json.hostname || "unknown",
  role: $json.role || "unknown"
};

// è§£æž LLDP é‚»å±…
let lldpList = [];
try {
  lldpList = JSON.parse($json.lldp_neighbors || "[]");
} catch (e) {
  lldpList = [];
}

// ðŸ” ä»Ž $json.devices æž„é€  hostname â†’ role æ˜ å°„ï¼ˆå‰ç«¯ webhook å¸¦è¿›æ¥çš„ï¼‰
const hostnameToRole = {};
if (Array.isArray($json.devices)) {
  for (const d of $json.devices) {
    hostnameToRole[d.hostname] = d.role;
  }
}

const seen = new Set();
const links = [];

for (const neighbor of lldpList) {
  const targetName = neighbor.remote_device || "unknown";
  const targetRole = hostnameToRole[targetName] || "unknown";

  const key = `${node.hostname}_${targetName}_${neighbor.local_port}_${neighbor.remote_port}`;
  if (seen.has(key)) continue;
  seen.add(key);

  links.push({
    source: node.hostname,
    target: targetName,
    sourcePort: neighbor.local_port || null,
    targetPort: neighbor.remote_port || null,
    targetRole: targetRole
  });
}

// âœ… è¾“å‡ºç¬¦åˆ Each æ¨¡å¼çš„ç»“æž„
return {
  json: {
    task_name: `topology_${Date.now()}`,
    topology: {
      node,
      links
    }
  }
};

const input = $input.all();

// === åˆå¹¶æ‰€æœ‰ links åˆ°ä¸€ä¸ªæ•°ç»„ ===
const allLinks = [];
for (const item of input) {
  const topology = item.json.topology || {};
  const node = topology.node || {};
  const links = topology.links || [];

  for (const link of links) {
    allLinks.push({
      ...link,
      _sourceIp: node.ip // ç”¨äºŽåŽç»­æŸ¥é‡å®šä½
    });
  }
}

// === ç”¨ Set åŽ»é‡ï¼šåŸºäºŽ source + target + port ===
const seen = new Set();
const dedupedLinks = [];

for (const link of allLinks) {
  const key = [link.source, link.target, link.sourcePort, link.targetPort].join('|');
  const reverseKey = [link.target, link.source, link.targetPort, link.sourcePort].join('|');

  if (seen.has(key) || seen.has(reverseKey)) continue;

  seen.add(key);
  dedupedLinks.push(link);
}

// === åªä¿ç•™æœ€ç»ˆ dedup åŽçš„ linksï¼Œnodes ä»ç„¶ä»ŽåŽŸå§‹æ¥æºæ¥ç”¨ ===
const firstTopology = input[0].json.topology || {};
const nodes = input.map(item => item.json.topology?.node).filter(Boolean);

return [{
  json: {
    task_name: `topology_${Date.now()}`,
    nodes,
    links: dedupedLinks
  }
}];
