  // æ£€æµ‹ ARP è¡¨å¤´
  if (line.toLowerCase().includes("macaddress") && line.toLowerCase().includes("iface")) {
    inArpSection = true;
    // ä¿å­˜å­—æ®µèµ·å§‹ä½ç½®
    fieldIndexes = [...line.matchAll(/\S+/g)].map(m => m.index);
    fieldNames = [...line.match(/\S+/g)].map(normalizeField);
