  }

  // Match route lines that start with protocol codes
  const routeMatch = line.match(/^([SKCREOITBVAFPN])([*>\s]*)\s*(\d+\.\d+\.\d+\.\d+\/\d+)\s*(.*)$/);

  if (routeMatch) {
    const [, protocol, flags, network, details] = routeMatch;

    currentRoute = {
      prefix: network,
      protocol: protocol,
      paths: []
    };

    const isActive = flags.includes('*');
    const isInstalled = flags.includes('>');

    // Parse the route details
    if (details) {
      parseRouteDetails(currentRoute, details, isActive, isInstalled);
    }

    routes.push(currentRoute);
  }
  // Handle continuation lines (lines that don't start with a protocol code)
  else if (currentRoute && line.match(/^\s*\[.*\]|via\s+|is directly connected/)) {
    parseRouteDetails(currentRoute, line, true, true);
  }
}

function parseRouteDetails(route, details, isActive, isInstalled) {
  details = details.trim();

  // Handle "via" routes (next-hop routes)
  const viaMatch = details.match(/\[([^\]]+)\]\s+via\s+([\d.]+)(?:,\s*(\w+))?(?:,\s*(.+))?/);
  if (viaMatch) {
    const [adminDist, metric] = viaMatch[1].split('/');

    const pathObj = {
      active: isActive,
      installed: isInstalled,
      metric: `${adminDist}/${metric || '0'}`,
      weight: 1,
      nexthop: viaMatch[2]
    };

    if (viaMatch[3]) {
      pathObj.interface = viaMatch[3];
    }

    if (viaMatch[4]) {
      pathObj.age = viaMatch[4];
    }

    route.paths.push(pathObj);
    return;
  }

  // Handle directly connected routes
  const connectedMatch = details.match(/is directly connected,\s*(\w+)(?:,\s*(.+))?/);
  if (connectedMatch) {
    const pathObj = {
      active: isActive,
      connected: true,
      installed: isInstalled,
      interface: connectedMatch[1] + ","
    };

    route.paths.push(pathObj);
    return;
  }

  // Handle other route types with basic parsing
  if (details.length > 0 && !details.includes('unreachable') && !details.includes('rejected')) {
    // Try to extract basic info for other route types
    const basicMatch = details.match(/(\w+)(?:,\s*(.+))?/);
    if (basicMatch) {
      const pathObj = {
        active: isActive,
        installed: isInstalled
      };

      // If it looks like an interface, treat as connected
      if (basicMatch[1].match(/^(eth|Ethernet|Loopback|Vlan)/i)) {
        pathObj.connected = true;
        pathObj.interface = basicMatch[1] + ",";
      } else {
        pathObj.details = details;
      }

      route.paths.push(pathObj);
    }
  }
}

