function parsePercent(str) {
  if (!str || typeof str !== "string") return 0;
  const match = str.match(/([\d.]+)/);
  return match ? parseFloat(match[1]) : 0;
}

// é€Ÿç‡æ¢ç®—å‡½æ•°ä¿æŒä¸å˜ï¼ˆå¯é€‰ï¼‰
function parseRateToBitPerSec(str) {
  if (!str || typeof str !== 'string') return 0;
  const match = str.match(/([\d.]+)\s*(B|KB|MB|GB|TB)?\/s/i);
  if (!match) return 0;

  const value = parseFloat(match[1]);
  const unit = (match[2] || "B").toUpperCase();

  const multiplier = {
    B: 8,
    KB: 8 * 1e3,
    MB: 8 * 1e6,
    GB: 8 * 1e9,
    TB: 8 * 1e12
  };

  return value * (multiplier[unit] || 8);  // è¾“å‡º bit/s
}

function formatBitRate(bitsPerSec) {
  const units = ['b/s', 'Kb/s', 'Mb/s', 'Gb/s', 'Tb/s'];
  let value = bitsPerSec;
  let unitIndex = 0;

  while (value >= 1000 && unitIndex < units.length - 1) {
    value /= 1000;
    unitIndex++;
  }

  return `${value.toFixed(2)} ${units[unitIndex]}`;
}

