function parseSegment(segment) {
  const ports = [];
  const parts = segment.split('+').join(',').split(',');
  parts.forEach(part => {
    const match = part.trim().match(/(\d+)\*?(\d+G)/i);
    if (match) {
      const count = parseInt(match[1]);
      const speed = match[2].toUpperCase();
      ports.push({ count, speed });
    }
  });
  return ports;
}

function buildPortBlockSmart(portList, startIndex, xStart, maxCols, yStart, type) {
  const result = [];
  let current = 0;

  portList.forEach(port => {
    for (let i = 0; i < port.count; i++) {
      const index = startIndex + current;
      const col = current % maxCols;
      const row = Math.floor(current / maxCols);

      result.push({
        physical_port: `Eth${index + 1}`,
        x: xStart + col,
        y: yStart + row,
        speed: port.speed,
        type: type
      });

      current++;
    }
  });

  return result;
}

