function formatBytes(bytes) {
  if (bytes < 1024) return bytes + ' B';
  const units = ['KB', 'MB', 'GB', 'TB', 'PB'];
  let value = bytes / 1024;
  let i = 0;
  while (value >= 1024 && i < units.length - 1) {
    value = value / 1024;
    i++;
  }
  return value.toFixed(2) + ' ' + units[i];
}

const interfaceList = interfaces.map(i => {
  const state = i.state || {};
  const counters = state.counters || {};
  const eth = (i["openconfig-if-ethernet:ethernet"] || {}).state || {};
  const speedRaw = eth["port-speed"] || "";
  const speed = speedRaw.replace("openconfig-if-ethernet:SPEED_", "").replace("GB", "G");

  return {
    physical_port: i.config?.description || i.name,
    logical_port: i.name,
    status: state["oper-status"] || "",
    speed: speed || "",
    mtu: state["mtu"] || null,
    "in-errors": counters["in-errors"] || 0,
    "out-errors": counters["out-errors"] || 0,
    "in-discards": counters["in-discards"] || 0,
    "out-discards": counters["out-discards"] || 0,
    in_pkts_total: formatBytes(counters["in-octets"] || 0),
    out_pkts_total: formatBytes(counters["out-octets"] || 0)
  };
});

return {
  json: {
    ip_address,
    interfaces: interfaceList,
    timestamp: now
  }
